chrome.runtime.onInstalled.addListener(()=>{console.log("Chrome Extension is installed!")});chrome.tabs.onActivated.addListener((t,r)=>{chrome.scripting.executeScript({target:{tabId:r.id},files:["content.js"]}),chrome.tabs.query({active:!0,currentWindow:!0},o=>{const e=o[0];console.log("Current Tab URL:",e.url),e.url==="https://www.youtube.com/"&&chrome.tabs.update(e.id,{url:"https://www.vitejs.dev/"})})});chrome.tabs.onUpdated.addListener((t,r,o)=>{chrome.scripting.executeScript({target:{tabId:o.id},files:["content.js"]}),r.status==="complete"&&chrome.tabs.query({active:!0,currentWindow:!0},e=>{const c=e[0];console.log("Current Tab URL:",c.url),c.url==="https://www.youtube.com/"&&chrome.tabs.update(c.id,{url:"https://www.vitejs.dev/"})})});chrome.runtime.onMessage.addListener((t,r,o)=>{if(t.type==="extractedText"){console.log("Received Text from Page:",t.text);const e=t.text.split(" ").slice(0,100).join(" ");chrome.storage.local.set({extractedText:e},()=>{console.log("Text saved to local storage.")})}});
