chrome.runtime.onInstalled.addListener(()=>{console.log("Chrome Extension is installed!")});chrome.tabs.onActivated.addListener(s=>{chrome.tabs.query({active:!0,currentWindow:!0},e=>{const t=e[0];console.log("Current Tab URL:",t.url),t.url==="https://www.youtube.com/"&&chrome.tabs.update(t.id,{url:"https://www.vitejs.dev/"})})});chrome.tabs.onUpdated.addListener((s,e,t)=>{e.status==="complete"&&chrome.tabs.query({active:!0,currentWindow:!0},o=>{const r=o[0];console.log("Current Tab URL:",r.url),r.url==="https://www.youtube.com/"&&chrome.tabs.update(r.id,{url:"https://www.vitejs.dev/"})})});
